  'additionalHomeContent' => 
  array (
    'es_ES' => '<p><strong>Editor / Director: </strong>François Gagin</p><p><strong>Asistentes editoriales :</strong></p><p><strong></strong><span>Yhady Vanessa Gutiérrez Arias</span></p><p><span>José Julián Palomino Ortegón</span></p><p><strong>***</strong></p><p><strong>ISSN: 0120-4688</strong></p><p><strong>ISSN (D): 2389-9387</strong></p>',
